// import { createStore } from 'redux'
import { configureStore, createSlice } from '@reduxjs/toolkit'

const initialCounterState = {
  counter: 0, showCounter: true, showWarning: false,
}

const counterSlice = createSlice({
  name: 'counter',
  initialState: initialCounterState,
  reducers: {
    increment(state) {
      if (state.counter > -5) {
        state.showWarning = false
      }
      state.counter++  // this called as mutate the state
    },
    decrement(state) {
      if (state.counter <= -5) {
        state.counter = state.counter - 0
        // state use to display cwaring message
        state.showWarning = true
      } else {
        state.counter--
      }
    },
    increaseBy10(state, action) {
      // must be named as payload
      state.counter = state.counter + action.payload
    },
    reset(state) {
      state.counter = 0
    },
    toggleCounter(state) {
      state.showCounter = !state.showCounter
    },
  }
})

const initialAuthState = {
  isAuth: false,
}

const authSlice = createSlice({
  name: 'authentication',
  reducers: {
    login(state) { },
    logout(state) { },
  }
})

const CounterStore = configureStore({ reducer: counterSlice.reducer })

export const counterActions = counterSlice.actions

export default CounterStore




