import { useSelector, useDispatch } from 'react-redux'

import classes from './Counter.module.css';

const Counter = () => {
  const counterDispatch = useDispatch()
  // useSelector is to abstract data from store
  const counter = useSelector(state => state.counter)
  const show = useSelector(state => state.sshowCounter)

  const plusHandler = () => {
    counterDispatch({ type: 'increment', amount: 2 })
  }

  const minusHandler = () => {
    counterDispatch({ type: 'decrement', amount: 1 })
  }

  const resetHandler = () => counterDispatch({ type: 'reset' })

  const toggleCounterHandler = () => { counterDispatch({ type: 'toggle' }) };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {
        show && <div className={classes.value}>-- {counter} --</div>

      }
      <div>
        <button style={{ backgroundColor: 'red' }} onClick={minusHandler} >Minus</button>
        <button style={{ backgroundColor: 'grey' }} onClick={resetHandler} >Reset</button>
        <button style={{ backgroundColor: 'blue' }} onClick={plusHandler} >Plus</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
